// src/components/Leadscard.jsx
import React from "react";

const Leadscard = ({
  name,
  destination,
  travelDate,
  guest,
  budget,
  source,
  nextFollowUp,
  notes,
  status,
}) => {
  const statusColor = {
    New: "primary",
    "In Progress": "warning",
    "Follow-up": "info",
    Lost: "danger",
  }[status] || "secondary";

  return (
    <div className="col-md-4 mb-4">
      <div className="card border-0 shadow-sm rounded-4 h-100">
        <div className="card-body">
          {/* Header */}
          <div className="d-flex justify-content-between align-items-start mb-2">
            <h5 className="fw-bold mb-0">{name}</h5>
            <span className={`badge bg-${statusColor}`}>{status}</span>
          </div>

          {/* Destination */}
          <p className="text-muted mb-1">
            <i className="bi bi-geo-alt me-2 text-secondary"></i>
            {destination}
          </p>

          {/* Travel Dates */}
          <p className="mb-1">
            <i className="bi bi-calendar-event me-2 text-secondary"></i>
            {travelDate}
          </p>

          {/* Guest Count */}
          <p className="mb-1">
            <i className="bi bi-people me-2 text-secondary"></i>
            {guest} {guest > 1 ? "people" : "person"}
          </p>

          {/* Budget */}
          <p className="mb-1">
            <i className="bi bi-cash-coin me-2 text-secondary"></i>
            â‚¹{budget}
          </p>

          {/* Source */}
          <p className="mb-3">
            <i className="bi bi-link-45deg me-2 text-secondary"></i>
            {source}
          </p>

          {/* Next Follow-up */}
          {nextFollowUp && (
  <div className="alert alert-warning py-1 mb-2 mt-2">
    <small>
      <strong>Next Follow-up:</strong> {nextFollowUp}
    </small>
  </div>
)}

{notes && (
  <p className="text-muted small mb-2">
    <i className="bi bi-journal-text me-2"></i>
    {notes}
  </p>
)}


          {/* Notes */}
          <p className="text-muted small mb-3">{notes}</p>

          {/* Action Buttons */}
          <div className="d-flex justify-content-between align-items-center">
            <button className="btn btn-outline-secondary btn-sm rounded-3">
              <i className="bi bi-telephone"></i> Call
            </button>
            <button className="btn btn-success btn-sm rounded-3">
              <i className="bi bi-whatsapp"></i> WhatsApp
            </button>
            <button className="btn btn-outline-primary btn-sm rounded-3">
              <i className="bi bi-pencil"></i> Edit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Leadscard;
