import React from 'react'
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { set } from 'mongoose';

const AddLeadForm = () => {
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [destination, setDestination] = useState("");
    const [travelDate, setTravelDate] = useState("");
    const [guest, setGuest] = useState("");
    const [budget, setBudget] = useState("");
    const [email, setEmail] = useState("");
    const [createdAt, setCreatedAt] = useState("");
    const [source, setSource] = useState("");
    const [asignedTo, setAsignedTo] = useState("");
    const [Status, setStatus] = useState("");
    const [nextFollowUp, setNextFollowUp] = useState("");
    const [notes, setNotes] = useState("");
    const navigate = useNavigate();

    const handleAddLead = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
          const res = await axios.post("http://localhost:5000/data", {
            name, phone, destination, travelDate, guest, budget, email, createdAt, source, asignedTo, Status, nextFollowUp, notes
          });
            if (res.data.status === "ok") {
            alert("Lead added successfully!");
            navigate("/leads");
          } else {
            alert(res.data.error || "Failed to add lead");
          } 
        } catch (err) {
          alert("Failed to add lead. Please try again.");
        } finally {
          setLoading(false);
        }
        };


  return (
    <Modal show={show} onHide={handleClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Add New Lead</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Full Name *</Form.Label>
                <Form.Control
                  type="text"
                  name="fullName"
                  placeholder="Enter full name"
                  value={formData.fullName}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Contact Number *</Form.Label>
                <Form.Control
                  type="tel"
                  name="contactNumber"
                  placeholder="Enter contact number"
                  value={formData.contactNumber}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>WhatsApp Number</Form.Label>
                <Form.Control
                  type="tel"
                  name="whatsappNumber"
                  placeholder="Enter WhatsApp number"
                  value={formData.whatsappNumber}
                  onChange={handleChange}
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Enter email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Trip Name / Destination *</Form.Label>
                <Form.Control
                  type="text"
                  name="tripName"
                  placeholder="e.g. Bali Paradise Tour"
                  value={formData.tripName}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Travel Dates *</Form.Label>
                <Form.Control
                  type="text"
                  name="travelDates"
                  placeholder="e.g. 15–20 Jan 2025"
                  value={formData.travelDates}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Number of People *</Form.Label>
                <Form.Control
                  type="number"
                  name="numberOfPeople"
                  placeholder="e.g. 2"
                  value={formData.numberOfPeople}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Budget Range *</Form.Label>
                <Form.Control
                  type="text"
                  name="budgetRange"
                  placeholder="e.g. ₹80,000 – ₹1,00,000"
                  value={formData.budgetRange}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Source *</Form.Label>
                <Form.Select
                  name="source"
                  value={formData.source}
                  onChange={handleChange}
                >
                  <option>Website</option>
                  <option>Instagram</option>
                  <option>Referral</option>
                  <option>Meta Ads</option>
                </Form.Select>
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Assigned To *</Form.Label>
                <Form.Select
                  name="assignedTo"
                  value={formData.assignedTo}
                  onChange={handleChange}
                >
                  <option>Ayush</option>
                  <option>Nidhi</option>
                  <option>Travel Hub Team</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Lead Status *</Form.Label>
                <Form.Select
                  name="leadStatus"
                  value={formData.leadStatus}
                  onChange={handleChange}
                >
                  <option>New</option>
                  <option>In Progress</option>
                  <option>Booked</option>
                  <option>Lost</option>
                </Form.Select>
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Next Follow-up Date</Form.Label>
                <Form.Control
                  type="datetime-local"
                  name="followUpDate"
                  value={formData.followUpDate}
                  onChange={handleChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Notes</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              name="notes"
              placeholder="Any additional information about the lead..."
              value={formData.notes}
              onChange={handleChange}
            />
          </Form.Group>

          <div className="d-flex justify-content-end">
            <Button variant="secondary" onClick={handleClose} className="me-2">
              Cancel
            </Button>
            <Button type="submit" variant="warning" style={{ background: "#ff7a00", border: "none" }}>
              Add Lead
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default AddLeadForm;
