import React from "react";

const LeadCard = ({
  name,
  destination,
  travelDate,
  guest,
  budget,
  source,
  nextFollowUp,
  notes,
  status,
}) => {
  const statusColor = {
    New: "primary",
    "In Progress": "warning",
    "Follow-up": "info",
    Lost: "danger",
  }[status] || "secondary";

  // ✅ Convert nextFollowUp ISO string to readable format
  const formattedFollowUp = nextFollowUp
    ? new Date(nextFollowUp).toLocaleString("en-IN", {
        day: "2-digit",
        month: "short",
        year: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      })
    : null;

  return (
    <div className="col-md-4 mb-4">
      <div className="card border-0 shadow-sm rounded-4 h-100">
        <div className="card-body">
          <div className="d-flex justify-content-between align-items-start mb-2">
            <h5 className="fw-bold mb-0">{name}</h5>
            <span className={`badge bg-${statusColor}`}>{status}</span>
          </div>

          <p className="text-muted mb-1">
            <i className="bi bi-geo-alt me-2"></i> {destination}
          </p>
          <p className="mb-1">
            <i className="bi bi-calendar-event me-2"></i> {travelDate}
          </p>
          <p className="mb-1">
            <i className="bi bi-people me-2"></i> {guest} {guest > 1 ? "people" : "person"}
          </p>
          <p className="mb-1">
            <i className="bi bi-cash-coin me-2"></i> ₹{budget}
          </p>
          <p className="mb-3">
            <i className="bi bi-megaphone me-2"></i> {source}
          </p>
          <p className="mb-3" style={{ color: "#000000ff", fontWeight: "500", background: "#fff3e0", padding: "5px", borderRadius: "5px" }}>
            <i className="bi bi-megaphone me-2">Next Follow up:- </i> {nextFollowUp}
          </p>

          

          {notes && (
            <p className="text-muted small mb-3">
              <i className="bi bi-journal-text me-2"></i>
              {notes}
            </p>
          )}

          <div className="d-flex justify-content-between">
            <button className="btn btn-outline-secondary btn-sm">
              <i className="bi bi-telephone"></i> Call
            </button>
            <button className="btn btn-success btn-sm">
              <i className="bi bi-whatsapp"></i> WhatsApp
            </button>
            <button className="btn btn-outline-primary btn-sm">
              <i className="bi bi-pencil"></i> Edit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LeadCard;
