// src/pages/Dashboard.jsx
import React, { useState, useEffect } from "react";
import Header from "./Header";
import LeadCard from "../components/Leadscard";
import StatsCard from "./Statscard";
import AddLeadForm from "../components/AddLeadForm";
import axios from "axios";

const Dashboards = () => {
  const [leads, setLeads] = useState([]);
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  // ðŸ§  Fetch Leads from Backend
  useEffect(() => {
    const fetchLeads = async () => {
      try {
        const res = await axios.get("http://localhost:5000/data");
        setLeads(res.data);
      } catch (err) {
        console.error("Error fetching leads:", err);
      }
    };
    fetchLeads();
  }, []);

  const stats = [
    { title: "Total Leads", count: leads.length, subtext: "+3 this week", icon: "bi-person", color: "#0d6efd" },
    { title: "Booked", count: leads.filter(l => l.status === "Booked").length, subtext: "33% conversion", icon: "bi-check-circle", color: "#198754" },
    { title: "Lost", count: leads.filter(l => l.status === "Lost").length, subtext: "Last 30 days", icon: "bi-x-circle", color: "#dc3545" },
    { title: "Today's Follow-ups", count: leads.filter(l => l.nextFollowUp).length, subtext: "4 pending", icon: "bi-calendar-event", color: "#fd7e14" },
  ];

  return (
    <div className="bg-light min-vh-100">
      <Header />

      <div className="container py-4">
        {/* Stats Section */}
        <div className="row mb-4">
          {stats.map((s, i) => (
            <StatsCard
              key={i}
              title={s.title}
              count={s.count}
              subtext={s.subtext}
              icon={<i className={`bi ${s.icon} fs-4`}></i>}
              color={s.color}
            />
          ))}
        </div>

        {/* Leads Section */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-bold">Leads</h4>
          <button className="btn btn-warning fw-semibold" onClick={handleShow}>
            <i className="bi bi-plus-lg me-1"></i> Add New Lead
          </button>
        </div>

        <div className="row">
          {leads.length > 0 ? (
            leads.map((lead, i) => <LeadCard key={i} {...lead} />)
          ) : (
            <p className="text-center text-muted mt-4">No leads yet</p>
          )}
        </div>

        {/* Add Lead Modal */}
        <AddLeadForm show={showModal} handleClose={handleClose} />
      </div>
    </div>
  );
};

export default Dashboards;
