// src/pages/Dashboard.jsx
import React from "react";
import Header from "./Header";
import LeadCard from "../components/Leadscard";
import StatsCard from "./Statscard";
import AddLeadForm from "../components/AddLeadForm";
import axios from "axios";
import { useEffect, useState } from "react";



const Dashboards = () => {
  const stats = [
    { title: "Total Leads", count: 24, subtext: "+3 this week", icon: "bi-person", color: "#0d6efd" },
    { title: "Booked", count: 8, subtext: "33% conversion", icon: "bi-check-circle", color: "#198754" },
    { title: "Lost", count: 5, subtext: "Last 30 days", icon: "bi-x-circle", color: "#dc3545" },
    { title: "Today's Follow-ups", count: 6, subtext: "4 pending", icon: "bi-calendar-event", color: "#fd7e14" },
  ];

  const [leads, setLeads] = useState([]);
  useEffect(() =>{
    const fetchLeads = async () => {
      try {
        const response = await axios.get("http://localhost:5000/data"); // Adjust the URL as needed
        if (response.data.status === "ok") {
          setLeads(response.data.data);
        } else {
          console.error("Failed to fetch leads:", response.data.error);
        }
      } catch (error) {
        console.error("Error fetching leads:", error);
      }
    };
    fetchLeads();
  }, []);

  const [showForm, setShowForm] = useState(false);

  return (
    <div className="bg-light min-vh-100">
      <Header />

      <div className="container py-4">
        <div className="row mb-4">
          {stats.map((s, i) => (
            <StatsCard
              key={i}
              title={s.title}
              count={s.count}
              subtext={s.subtext}
              icon={<i className={`bi ${s.icon} fs-4`}></i>}
              color={s.color}
            />
          ))}
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-bold">Leads</h4>
          <button
            className="btn btn-warning fw-semibold"
            onClick={() => setShowForm(true)}>
            <i className="bi bi-plus-lg me-1"></i> Add New Lead
          </button>
         
      </div>

      <div className="row">
        {leads.map((lead, i) => (
          <LeadCard key={i} {...lead} />
        ))}
      </div>
    </div>

    <AddLeadForm
  show={showForm}
  handleClose={() => setShowForm(false)}
/>

    </div >
  );
};

export default Dashboards;
