// src/pages/Dashboard.jsx
import React from "react";
import Header from "./Header";
import LeadCard from "../components/Leadscard";
import StatsCard from "./Statscard";
import AddLeadForm from "../components/AddLeadForm";
import { useState } from "react";


const Dashboards = () => {
  const stats = [
    { title: "Total Leads", count: 24, subtext: "+3 this week", icon: "bi-person", color: "#0d6efd" },
    { title: "Booked", count: 8, subtext: "33% conversion", icon: "bi-check-circle", color: "#198754" },
    { title: "Lost", count: 5, subtext: "Last 30 days", icon: "bi-x-circle", color: "#dc3545" },
    { title: "Today's Follow-ups", count: 6, subtext: "4 pending", icon: "bi-calendar-event", color: "#fd7e14" },
  ];

  const leads = [
    {
      name: "Rahul Sharma",
      trip: "Bali Paradise Tour",
      dates: "15–20 Jan 2025",
      people: 2,
      budget: "80,000 – 1,00,000",
      source: "Meta Ads",
      followUp: "2025-01-10 10:00 AM",
      notes: "Interested in beach resorts and water sports.",
      status: "New",
    },
    {
      name: "Priya Mehta",
      trip: "Switzerland Honeymoon Package",
      dates: "10–17 Feb 2025",
      people: 2,
      budget: "2,50,000 – 3,00,000",
      source: "Instagram",
      followUp: "2025-01-09 03:00 PM",
      notes: "Loves snow and romantic destinations.",
      status: "In Progress",
    },
    {
      name: "Amit Patel",
      trip: "Dubai Family Trip",
      dates: "5–10 Mar 2025",
      people: 4,
      budget: "1,50,000 – 2,00,000",
      source: "Referral",
      followUp: "2025-01-09 11:00 AM",
      notes: "Travelling with kids, wants theme parks.",
      status: "Follow-up",
    },
  ];

  const [showForm, setShowForm] = useState(false);

  return (
    <div className="bg-light min-vh-100">
      <Header />

      <div className="container py-4">
        <div className="row mb-4">
          {stats.map((s, i) => (
            <StatsCard
              key={i}
              title={s.title}
              count={s.count}
              subtext={s.subtext}
              icon={<i className={`bi ${s.icon} fs-4`}></i>}
              color={s.color}
            />
          ))}
        </div>

        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-bold">Leads</h4>
          <button
            className="btn btn-warning fw-semibold"
            onClick={() => setShowForm(true)}>
            <i className="bi bi-plus-lg me-1"></i> Add New Lead
          </button>
         
      </div>

      <div className="row">
        {leads.map((lead, i) => (
          <LeadCard key={i} {...lead} />
        ))}
      </div>
    </div>

    {showForm && (
  <AddLeadForm
    onClose={() => setShowForm(false)}
    onSubmit={(newLead) => {
      console.log("New lead added:", newLead);
      setShowForm(false);
      // Later you'll add backend call + state update here
    }}
  />
)}

    </div >
  );
};

export default Dashboards;
