import React, { useState } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddLeadForm = ({ show, handleClose }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [destination, setDestination] = useState("");
  const [travelDate, setTravelDate] = useState("");
  const [guest, setGuest] = useState("");
  const [budget, setBudget] = useState("");
  const [email, setEmail] = useState("");
  const [createdAt, setCreatedAt] = useState("");
  const [source, setSource] = useState("");
  const [assignedTo, setAssignedTo] = useState("");
  const [status, setStatus] = useState("");
  const [nextFollowUp, setNextFollowUp] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const handleAddLead = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/data", {
        name,
        phone,
        destination,
        travelDate,
        guest,
        budget,
        email,
        createdAt,
        source,
        assignedTo,
        status,
        nextFollowUp,
        notes,
      });

      if (res.data.status === "ok") {
        alert("Lead added successfully!");
        navigate("/leads");
        handleClose();
      } else {
        alert(res.data.error || "Failed to add lead");
      }
    } catch (err) {
      alert("Failed to add lead. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Add New Lead</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form onSubmit={handleAddLead}>
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Full Name *</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter full name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Contact Number *</Form.Label>
                <Form.Control
                  type="tel"
                  placeholder="Enter contact number"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          {/* More fields below */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Destination *</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Trip destination"
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Travel Date *</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="e.g. 15–20 Jan 2025"
                  value={travelDate}
                  onChange={(e) => setTravelDate(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Guests *</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="No. of people"
                  value={guest}
                  onChange={(e) => setGuest(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Budget *</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="e.g. ₹80,000 – ₹1,00,000"
                  value={budget}
                  onChange={(e) => setBudget(e.target.value)}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

            <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Created At</Form.Label>
                <Form.Control
                  type="date"
                  value={createdAt}
                  onChange={(e) => setCreatedAt(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>

            <Row className="mb-3">
            <Col md={6}>
                <Form.Group>
                <Form.Label>Source</Form.Label>
                <Form.Select
                    value={source}
                    onChange={(e) => setSource(e.target.value)}
                >
                    <option value="">Select source</option>
                    <option value="Instagram">Instagram</option>
                    <option value="Facebook">Facebook</option>
                    <option value="Google Ads">Google Ads</option>
                    <option value="Referral">Referral</option>
                    <option value="Meta Ads">Meta Ads</option>
                    <option value="Walk-in">Walk-in</option>
                    <option value="Other">Other</option>
                </Form.Select>
                </Form.Group>
            </Col>



            <Col md={6}>
              <Form.Group>
                <Form.Label>Assigned To</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Staff member"
                  value={assignedTo}
                  onChange={(e) => setAssignedTo(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>

            <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>Status</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="e.g. New, In Progress"
                  value={status}
                  onChange={(e) => setStatus(e.target.value)}
                />
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group>
                <Form.Label>Next Follow-up</Form.Label>
                <Form.Control
                  type="datetime-local"
                  value={nextFollowUp}
                  onChange={(e) => setNextFollowUp(e.target.value)}
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Notes</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Any additional info..."
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </Form.Group>

          <div className="d-flex justify-content-end">
            <Button variant="secondary" onClick={handleClose} className="me-2">
              Cancel
            </Button>
            <Button
              type="submit"
              variant="warning"
              disabled={loading}
              style={{ background: "#ff7a00", border: "none" }}
            >
              {loading ? "Saving..." : "Add Lead"}
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default AddLeadForm;
