// src/pages/Dashboards.js
import React, { useState, useEffect } from "react";
import Header from "./Header";
import LeadCard from "../components/Leadscard";
import StatsCard from "./Statscard";
import AddLeadForm from "../components/AddLeadForm";
import axios from "axios";

const Dashboards = () => {
  const stats = [
    { title: "Total Leads", count: 24, subtext: "+3 this week", icon: "bi-person", color: "#0d6efd" },
    { title: "Booked", count: 8, subtext: "33% conversion", icon: "bi-check-circle", color: "#198754" },
    { title: "Lost", count: 5, subtext: "Last 30 days", icon: "bi-x-circle", color: "#dc3545" },
    { title: "Today's Follow-ups", count: 6, subtext: "4 pending", icon: "bi-calendar-event", color: "#fd7e14" },
  ];

  // ✅ Declare leads state at top
  const [leads, setLeads] = useState([]);
  const [showForm, setShowForm] = useState(false);

  // ✅ Fetch leads from backend
  useEffect(() => {
    const fetchLeads = async () => {
      try {
        const response = await axios.get("http://localhost:5000/data");
        setLeads(response.data); // direct array
      } catch (error) {
        console.error("Error fetching leads:", error);
      }
    };
    fetchLeads();
  }, []);

  return (
    <div className="bg-light min-vh-100">
      <Header />

      <div className="container py-4">
        {/* Stats */}
        <div className="row mb-4">
          {stats.map((s, i) => (
            <StatsCard
              key={i}
              title={s.title}
              count={s.count}
              subtext={s.subtext}
              icon={<i className={`bi ${s.icon} fs-4`}></i>}
              color={s.color}
            />
          ))}
        </div>

        {/* Header + Add button */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h4 className="fw-bold">Leads</h4>
          <button
            className="btn btn-warning fw-semibold"
            onClick={() => setShowForm(true)}
          >
            <i className="bi bi-plus-lg me-1"></i> Add New Lead
          </button>
        </div>

        {/* Leads List */}
        <div className="row">
          {leads.length > 0 ? (
            leads.map((lead, i) => <LeadCard key={i} {...lead} />)
          ) : (
            <p className="text-muted text-center">No leads found.</p>
          )}
        </div>
      </div>

      {/* Add Lead Modal */}
      <AddLeadForm show={showForm} handleClose={() => setShowForm(false)} />
    </div>
  );
};

export default Dashboards;
