// src/components/LeadCard.jsx
import React from "react";

const LeadCard = ({
  name,
  destination,
  travelDate,
  guest,
  budget,
  source,
  nextFollowUp,
  notes,
  status,
}) => {
  const statusColor = {
    New: "primary",
    "In Progress": "warning",
    "Follow-up": "success",
    Lost: "danger",
  }[status] || "secondary";

  return (
    <div className="col-md-4 mb-4">
      <div className="card shadow-sm border-0 h-100">
        <div className="card-body">
          <div className="d-flex justify-content-between align-items-start mb-2">
            <h5 className="fw-bold mb-0">{name}</h5>
            <span className={`badge bg-${statusColor}`}>{status}</span>
          </div>

          <p className="text-muted mb-1">
            <i className="bi bi-geo-alt me-2"></i> {destination}
          </p>

          <p className="mb-1">
            <i className="bi bi-calendar-event me-2"></i> {travelDate}
          </p>

          <p className="mb-1">
            <i className="bi bi-people me-2"></i> {guest}
          </p>

          <p className="mb-1">
            <i className="bi bi-cash-coin me-2"></i> â‚¹{budget}
          </p>

          <p className="mb-3">
            <i className="bi bi-link-45deg me-2"></i> {source}
          </p>

          {nextFollowUp && (
            <div className="alert alert-warning py-1 mb-3">
              <small>
                <strong>Next Follow-up:</strong> {nextFollowUp}
              </small>
            </div>
          )}

          {notes && (
            <p className="text-muted small mb-3">
              <i className="bi bi-journal-text me-2"></i>
              {notes}
            </p>
          )}

          <div className="d-flex justify-content-between">
            <button className="btn btn-outline-secondary btn-sm">
              <i className="bi bi-telephone"></i> Call
            </button>
            <button className="btn btn-success btn-sm">
              <i className="bi bi-whatsapp"></i> WhatsApp
            </button>
            <button className="btn btn-outline-primary btn-sm">
              <i className="bi bi-pencil"></i> Edit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LeadCard;
